import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function AddTheater() {
  // State variables to hold form field values and validation errors
  const [theaterInfo, setTheaterInfo] = useState({
    theaterId: "",
    theaterName: "",
    location: "",
    totalSeats: "",
    screenCount: ""
  });
  const [errors, setErrors] = useState({});

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Validation logic
    const validationErrors = {};
    if (!theaterInfo.theaterId) {
      validationErrors.theaterId = "Theater ID is required";
    }
    // Add more validation logic for other fields if needed
    
    // If there are validation errors, set them in state and prevent form submission
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    // If no errors, proceed with submitting the form data
    console.log("Form submitted:", theaterInfo);
    // You can add logic here to send the theaterInfo to the server
  };

  // Function to handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTheaterInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value
    }));
    // Clear the error message for the field being changed
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: ""
    }));
  };

  return (
    <div>
      <ul className="navbar navbar-expand-sm bg-light mb-3">
        <div className="topnav">
          <div className="topnav-right">
            <li className="nav-item search-field">
              <input className="search" type="text" placeholder="Search" />
            </li>
            <li className="nav-item">
              <Link to="/logout">Logout</Link>
            </li>
          </div>
        </div>
      </ul>
      <h1>Add Theatre</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="theaterId">Theater ID:</label>
          <input
            type="text"
            id="theaterId"
            name="theaterId"
            value={theaterInfo.theaterId}
            onChange={handleChange}
          />
          <p className="error">{errors.theaterId}</p>
        </div>
        <div>
          <label htmlFor="theaterName">Theater Name:</label>
          <input
            type="text"
            id="theaterName"
            name="theaterName"
            value={theaterInfo.theaterName}
            onChange={handleChange}
          />
          {/* Add similar input fields for other theater information */}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
