import { useReducer, useState } from "react";
import { Form, FormGroup, FormLabel, Button, Image } from 'react-bootstrap';
import { useNavigate } from "react-router-dom";
import { Link} from "react-router-dom";

export default function AddMovie(){

  const MovieForm = () => {
    const [movieData, setMovieData] = useState({
      title: '',
      director: '',
      releaseDate: '',
      genre: '',
      duration: '',
      language: ''
    });
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setMovieData(prevState => ({
        ...prevState,
        [name]: value
      }));
    };
  
    const handleSubmit = (e) => {
      e.preventDefault();
      console.log('Submitted Movie Data:', movieData);
      // You can perform further actions here like sending the data to a server
    };
  
    const handleReset = () => {
      setMovieData({
        title: '',
        director: '',
        releaseDate: '',
        genre: '',
        duration: '',
        language: ''
      });
    };
  
    return (
      <form onSubmit={handleSubmit}>
        <label>
          Movie ID:
          <input type="text" name="movieId" value={movieData.movieId} onChange={handleChange} />
        </label>
        <br />
        <label>
          Title:
          <input type="text" name="title" value={movieData.title} onChange={handleChange} />
        </label>
        <br />
        <label>
          Director:
          <input type="text" name="director" value={movieData.director} onChange={handleChange} />
        </label>
        <br />
        <label>
          Release Date:
          <input type="text" name="releaseDate" value={movieData.releaseDate} onChange={handleChange} />
        </label>
        <br />
        <label>
          Genre:
          <input type="text" name="genre" value={movieData.genre} onChange={handleChange} />
        </label>
        <br />
        <label>
          Duration:
          <input type="text" name="duration" value={movieData.duration} onChange={handleChange} />
        </label>
        <br />
        <label>
          Language:
          <input type="text" name="language" value={movieData.language} onChange={handleChange} />
        </label>
        <br />
        <button type="submit">Submit</button>
        <button type="button" onClick={handleReset}>Reset</button>
      </form>
    );
  };
    
  
          
    
  }
