import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Container, Form, Button, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

export default function AddTheater() {
  const [theaterInfo, setTheaterInfo] = useState({
    theaterName: "",
    location: "",
    totalSeats: "",
    screenCount: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTheaterInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Theater info submitted:", theaterInfo);
    // You can add logic here to send the theaterInfo to the server

    fetch("http://localhost:8080/addMovie", reqOption)
      .then((resp) => resp.text())
      .then((data) => {
        
        if (data.length > 0) {
          nav("/theatreAdmin");
        } else {
          alert("Something went wrong");
        }
      });
  };

  

  return (
    <Container>
      <Row className="justify-content-md-center">
        <Col xs={12} md={6}>
          <h1 className="text-center mb-4">Add Theatre</h1>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="theaterName">
              <Form.Label>Theater Name:</Form.Label>
              <Form.Control
                type="text"
                name="theaterName"
                value={theaterInfo.theaterName}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group controlId="location">
              <Form.Label>Location:</Form.Label>
              <Form.Control
                type="text"
                name="location"
                value={theaterInfo.location}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group controlId="totalSeats">
              <Form.Label>Total Seats:</Form.Label>
              <Form.Control
                type="number"
                name="totalSeats"
                value={theaterInfo.totalSeats}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group controlId="screenCount">
              <Form.Label>Screen Count:</Form.Label>
              <Form.Control
                type="number"
                name="screenCount"
                value={theaterInfo.screenCount}
                onChange={handleChange}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Col>
      </Row>
      <Row className="mt-3 justify-content-md-center">
        <Col xs="auto">
          <Link to="/logout">Logout</Link>
        </Col>
      </Row>
    </Container>
  );
}
